    public void startApp() throws MIDletStateChangeException {
        
        this.mainForm = new Form("Connection test");
        this.mainForm.append("Connection test, max "+this.testString.length()+".");
        Display.getDisplay(this).setCurrent(this.mainForm);
        this.mainForm.append(si);
        
        SocketConnection con = null;
        BufferedInputStream in = null;
        BufferedOutputStream out = null;
        try {
            con = (SocketConnection)
                         Connector.open("socket://131.177.37.234:10500",Connector.READ_WRITE,true);
            in = new cascom.fipa.util.BufferedInputStream(con.openInputStream());
            //in = con.openInputStream();
            out = new cascom.fipa.util.BufferedOutputStream(con.openOutputStream());
        } catch (Exception e){
            this.mainForm.append("Error:"+e.getMessage());
            return;
        }
        
        int i=0;
        int k = 0;
        byte res = -1;

        
    
        try {

        for(i =1000; i < this.testString.length(); i+=1000){
                si.setText(""+i);
                byte[] b = (this.testString.substring(0, i)+"\r\n").getBytes();
                out.write(b);
                out.flush();
                in.setNextEOS(b.length-5);
                for(k=0; k < b.length; k++){                    
                    res = (byte)in.read();
                    if(b[k] != res){
                        this.mainForm.append("No match i "+i+", k"+k);                        
                        //return;
                    }
                }
                
            }    
                this.mainForm.append("Test ended");
        } catch (Exception e){
            this.mainForm.append("Error in test 1, i "+i+":"+e.getMessage());
            return;
        }
    }
    
    
            
